import React, { useEffect, useState, useRef } from 'react';
import { Power3, gsap } from 'gsap';
import { ParallaxProvider, Parallax } from 'react-scroll-parallax';
import scape from './scape.jpg';
import background from './background.jpg'; // Additional background image
import midground from './midground.png'; // Additional middle layer image
import './App.css';
import ImageText from './components/ImageText';
import TextImage from './components/TextImage';
import sex_white from './sex_white.jpeg';
import cursed from './cursed_image.JPEG';
import jambi from './jambi.jpeg';

const positivity = [
    "こんにちは",
    "stay frosty",
    "i'm here",
    "hola",
    "good day!",
    '<3',
    '(╯°□°)╯',
    'ノ( º _ ºノ)'
];

const App = () => {
    const [caption, setCaption] = useState('');
    const [captionIndex, setCaptionIndex] = useState(0);
    const canvasRef = useRef(null);
    const navlinksRef = useRef(null);

    const handleButtonClick = () => {
        const element = document.getElementById('your-element-id');
        element.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        const slider = document.getElementById('slider');
        const captionElement = document.getElementById('caption');

        slider.classList.toggle('fade');
        captionElement.classList.toggle('fade');

        const shuffleArray = (array) => {
            for (let i = array.length - 1; i > 0; i--) {
                let j = Math.floor(Math.random() * (i + 1));
                [array[i], array[j]] = [array[j], array[i]];
            }
        };

        const changeCaption = () => {
            shuffleArray(positivity);
            const captionElement = document.getElementById('caption');
            captionElement.style.opacity = '0';
            setTimeout(() => {
                let newCaption = positivity[captionIndex];
                setCaption(newCaption);
                setCaptionIndex((prevIndex) => (prevIndex + 1) % positivity.length);
                captionElement.style.opacity = '1';
            }, 1000);
        };

        const interval = setInterval(changeCaption, 4000);

        window.addEventListener('scroll', scrollEffect);

        scrollEffect();

        const tl = gsap.timeline();

        tl.fromTo(canvasRef.current, 2, { height: "0%" }, { height: "80%", ease: Power3.easeInOut })
            .fromTo(navlinksRef.current, .75, { height: "0%" }, { height: "8%", ease: Power3.easeInOut });

        return () => {
            clearInterval(interval);
            window.removeEventListener('scroll', scrollEffect);
        };
    }, []);

    const scrollEffect = () => {
        const CONTAINER = document.querySelector('.character-information');
        if (window.scrollY >= 150) {
            CONTAINER.style.opacity = '1';
            CONTAINER.style.transform = 'translateX(0px)';
            CONTAINER.style.transition = '1s ease-in-out';
        } else {
            CONTAINER.style.opacity = '0';
            CONTAINER.style.transform = 'translate(-20px)';
        }
    };

    return (
        <ParallaxProvider>
            <div>
                <header>
                    <nav>
                        <ul className="nav-links" ref={navlinksRef}>
                            <li><a id="link" href="mailto:thomasolivermerritt@gmail.com?subject=Feedback&body=Message">EMAIL</a></li>
                            <li><a id="link" href="https://www.linkedin.com/in/thomas-merritt-6630ab232?trk=people-guest_people_search-card">LINKEDIN</a></li>
                            <li><a id="link" href="resume.pdf">RESUME</a></li>
                            <li><a id="link" href="https://github.com/ThomasMerritt">GITHUB</a></li>
                        </ul>
                    </nav>
                    <section>
                        <div className="canvas" ref={canvasRef}>
                            <img src={scape} width="1500" alt="Scape" />
                        </div>
                    </section>
                    <h1 id="caption" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>{caption}</h1>
                </header>

                <div id="slider"></div>

                <div className='character-information'>
                    <ImageText
                        image={cursed}
                        title="Thomas Merritt"
                        subtitle="Full Stack Developer"
                        description={[
                            "Hi, I'm Thomas. I'm a software engineer with a passion for creating and developing web applications.",
                            "I have experience with JavaScript, React, Redux, Ruby on Rails, and SQL.",
                            "I'm always looking for new opportunities to learn and grow as a developer.",
                            "I'm currently seeking a full-time position as a software engineer."
                        ]}
                    />
                    <TextImage
                        image={sex_white}
                        aboutContent={[
                            {
                                title: "About Me",
                                paragraphs: [
                                    "I graduated from the University of California, Santa Barbara with a Bachelor of Science in Biopsychology.",
                                ]
                            },
                            {
                                title: "My Skills",
                                paragraphs: [
                                    "JavaScript, React, Redux, Ruby on Rails, SQL, HTML, CSS, Git, Node.js, Express.js, PostgreSQL, MongoDB"
                                ]
                            },
                            {
                                title: "My Interests",
                                paragraphs: [
                                    "I enjoy hiking, playing video games, and watching movies in my free time.",
                                    "I'm also interested in machine learning and artificial intelligence."
                                ]
                            }
                        ]}
                    />
                    <ImageText
                        image={cursed}
                        title="Thomas Merritt"
                        subtitle="Full Stack Developer"
                        description={[
                            "Hi, I'm Thomas. I'm a software engineer with a passion for creating and developing web applications.",
                            "I have experience with JavaScript, React, Redux, Ruby on Rails, and SQL.",
                            "I'm always looking for new opportunities to learn and grow as a developer.",
                            "I'm currently seeking a full-time position as a software engineer."
                        ]}
                    />
                    <TextImage
                        image={jambi}
                        borderRadius="0%"
                        width='100%'
                        height='100%'
                        aboutContent={[
                            {
                                title: "About Me",
                                paragraphs: [
                                    "I graduated from the University of California, Santa Barbara with a Bachelor of Science in Biopsychology.",
                                ]
                            },
                            {
                                title: "My Skills",
                                paragraphs: [
                                    "JavaScript, React, Redux, Ruby on Rails, SQL, HTML, CSS, Git, Node.js, Express.js, PostgreSQL, MongoDB"
                                ]
                            },
                            {
                                title: "My Interests",
                                paragraphs: [
                                    "I enjoy hiking, playing video games, and watching movies in my free time.",
                                    "I'm also interested in machine learning and artificial intelligence."
                                ]
                            }
                        ]}
                    />
                </div>

                <div className='education-section'>
                    {/* Additional sections can go here */}
                </div>

                {/* Background Layer */}
                <Parallax y={[-30, 30]}>
                    <div style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100vh',
                        background: `url(${background}) no-repeat center center`,
                        backgroundSize: 'cover',
                        zIndex: -1
                    }}></div>
                </Parallax>

                {/* Middle Layer */}
                <Parallax y={[-20, 20]}>
                    <div style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100vh',
                        background: `url(${midground}) no-repeat center center`,
                        backgroundSize: 'cover',
                        zIndex: -1
                    }}></div>
                </Parallax>

                {/* Footer with Parallax Effect */}
                <Parallax y={[-10, 10]}>
                    <footer style={{
                        padding: '50px',
                        background: `url(${scape}) no-repeat center center`,
                        backgroundSize: 'cover',
                        color: '#fff',
                        textAlign: 'center'
                    }}>
                        <p>Scroll to see the parallax effect in action!</p>
                        <p>&copy; 2024 Thomas Merritt. All rights reserved.</p>
                    </footer>
                </Parallax>
            </div>
        </ParallaxProvider>
    );
};

export default App;
